import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { FlatList, Image, StyleSheet, Text, View, TouchableOpacity } from 'react-native';

import CreateCheckout from '../constants/checkout' 
import shopClient from '../constants/shopify'

import * as Linking from 'expo-linking';

import { connect } from 'react-redux';

function ProductPage({ navigation, route }) {
  const [Product, SetProduct] = React.useState([])
  const [Quantitiy, SetQuantity] = React.useState(1)

  
  const [CheckoutID, SetCheckoutID] = React.useState("")
  const [MyCheckout, SetMyCheckout] = React.useState([])
  const [CheckoutWebUrl, SetCheckoutWebUrl] = React.useState("")

  


  React.useEffect(() => {
    mapData()
  }, [])

  // console.log(CheckoutID)
  
  function AddToCart(item) {
    //   console.log(item.variants[0].id)

        CreateCheckout(SetCheckoutID)
        const lineItemsToAdd = [
          {
            variantId: item.variants[0].id,
            // variantId: "Z2lkOi8vc2hvcGlmeS9Qcm9kdWN0VmFyaWFudC8zODAwMjA4NjIxNTg3MQ==",
            quantity: Quantitiy,
            // customAttributes: [{key: "MyKey", value: "MyValue"}]
          }
        ]
          // console.log(item.variants)
          let checkoutItems = []
          let checkItem = {}
      
          if (CheckoutID != "") {
            shopClient.checkout.addLineItems(CheckoutID, lineItemsToAdd).then((checkout) => {
                console.log(checkout)
                for (let c = 0; c < checkout.lineItems.length; c++) {
                  checkItem = {title: checkout.lineItems[c].title}
                  checkoutItems = [ ...checkoutItems, checkItem ] 
          
                }
                SetCheckoutWebUrl(checkout.webUrl)
                // checkItem = {title: checkout.lineItems[0].title}
                // checkoutItems = [ ...checkoutItems, checkItem ] 
          
                // console.log(checkoutItems)
                SetMyCheckout(checkoutItems)
                console.log(CheckoutWebUrl)
          
              });
          }else{
              null
          }
      
          console.log(MyCheckout)
          if (CheckoutWebUrl !== ""){
            Linking.openURL(CheckoutWebUrl);
          }else{
            null
          }

        
      
  }

  // Fetch all products in your shop and map it to a state
const mapData = () => {
  // Fetching hapens here
  shopClient.product.fetch(route.params.itemId).then((product) => {

    // console.log(product)

    let productData = [{id: 0, images:[]}]


    // let ProductData = {
    //         title: product.title,
    //         available: product.available,
    //         description: product.description,
    //         images: product.images[0].src,
    //         id: product.id
    // }

    productData = [...productData, product]
    SetProduct(productData)
})}

const ProductTile = ({item}) => {

  if(item.id === 0){
      null
  }else{
    return(
        <View>
          <Text>{item.title}</Text>
          <Image source={{uri: item.images[0].src}} style={{height: 200, width: 200}}/>
          <Text>Description: </Text>
          <Text>{item.description}</Text>

          <TouchableOpacity onPress={() => SetQuantity(Quantitiy+1)}>
            <Text>Qnt: {Quantitiy}</Text>
          </TouchableOpacity>

          <TouchableOpacity onPress={() => AddToCart(item)}>
            <Text>Add to Cart</Text>
          </TouchableOpacity>
          
        </View>
    )
  }
}

  if(Product!=null) {
    return (
      <View style={styles.container}>
        <FlatList 
          data={Product}
          renderItem={ProductTile}
          keyExtractor={item => item.id}
          style={{paddingTop: 50}}
        />
        <StatusBar style="auto" />
      </View>
    );
  }else{
    return(
      <View>

      </View>
    )
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});

const mapStateToProps = (state) => {
  const { friends } = state
  return { friends }
};

export default connect(mapStateToProps)(ProductPage)
