import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { FlatList, Image, StyleSheet, Text, View } from 'react-native';

import shopClient from '../constants/shopify'

export default function ProductPage({ navigation, route }) {
  const [Product, SetProduct] = React.useState({})

  React.useEffect(() => {
    mapData()
  }, [])

  console.log(route.params.itemId)
  

  // Fetch all products in your shop and map it to a state
const mapData = () => {
  // Fetching hapens here
  shopClient.product.fetch(route.params.itemId).then((product) => {

    console.log(product)


    let ProductData = {
            title: product.title,
            available: product.available,
            description: product.description,
            images: product.images[0].src
    }
    SetProduct(ProductData)
})}

const ProductTile = ({item}) => {

  return(
      <View>
        <Text>{item.title}</Text>
        <Image source={{uri: item.images[0].imageSource}} style={{height: 200, width: 200}}/>
      </View>
  )
}

  if(Product!=null) {
    return (
      <View style={styles.container}>
        <FlatList 
          data={Product}
          renderItem={ProductTile}
          keyExtractor={item => item.id}
          style={{paddingTop: 50}}
        />
        <Text>Hello</Text>
        <StatusBar style="auto" />
      </View>
    );
  }else{
    return(
      <View>

      </View>
    )
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
