import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { FlatList, Image, StyleSheet, Text, View } from 'react-native';
import { TouchableOpacity } from 'react-native-gesture-handler';

import shopClient from '../constants/shopify'
import CreateCheckout from '../constants/checkout' 

export default function App() {
  const [Product, SetProduct] = React.useState([])
  const [CheckoutID, SetCheckoutID] = React.useState("")
  const [MyCheckout, SetMyCheckout] = React.useState([])


  React.useEffect(() => {
    mapData()
  }, [])



  // Fetch all products in your shop and map it to a state
const mapData = () => {
  // Fetching hapens here
  shopClient.product.fetchAll().then((products) => {
    let productWithImages=[]
    let productImages = {}

    for (let i = 0; i < products.length; i++) {

      for (let n = 0; n < products[i].images.length; n++) {
        productImages= {
          id: products[i].id,
          title: products[i].title,
          images: [
            {imageSource: products[i].images[n].src},
          ],
          variants: products[i].variants[0].id
        }

      }
      productWithImages = [ ...productWithImages, productImages ]

    }
    SetProduct(productWithImages)  })
}

console.log(CheckoutID)


const AddItemToCheckout = (item) => {

  CreateCheckout(SetCheckoutID)
  const lineItemsToAdd = [
    {
      variantId: item.variants,
      // variantId: "Z2lkOi8vc2hvcGlmeS9Qcm9kdWN0VmFyaWFudC8zODAwMjA4NjIxNTg3MQ==",
      quantity: 1,
      // customAttributes: [{key: "MyKey", value: "MyValue"}]
    }
  ]
    // console.log(item.variants)
    let checkoutItems = []
    let checkItem = {}

    shopClient.checkout.addLineItems(CheckoutID, lineItemsToAdd).then((checkout) => {
      // console.log(checkout.lineItems)
      for (let c = 0; c < checkout.lineItems.length; c++) {
        checkItem = {title: checkout.lineItems[c].title}
        console.log(checkout.lineItems[c].title + "This is title")
        checkoutItems = [ ...checkoutItems, checkItem ] 

      }

      // checkItem = {title: checkout.lineItems[0].title}
      // checkoutItems = [ ...checkoutItems, checkItem ] 

      console.log(checkoutItems)
      SetMyCheckout(checkoutItems)

    });

    console.log(MyCheckout)


}

const ProductTile = ({item}) => {

  return(
      <TouchableOpacity onPress={() => AddItemToCheckout(item)}>
        <Text>{item.title}</Text>
        <Image source={{uri: item.images[0].imageSource}} style={{height: 200, width: 200}}/>
      </TouchableOpacity>
  )
}

  if(Product!=null) {
    return (
      <View style={styles.container}>
        <FlatList 
          data={Product}
          renderItem={ProductTile}
          keyExtractor={item => item.id}
          style={{paddingTop: 50}}
        />
        <StatusBar style="auto" />
      </View>
    );
  }else{
    return(
      <View>

      </View>
    )
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
